        if (FeatureFlagReader.isFeatureEnabled(FeatureFlagConstants.ENABLE_CUSTOMER_VERIFICATION)) count++;
        if (FeatureFlagReader.isFeatureEnabled(FeatureFlagConstants.ENABLE_CUSTOMER_ACTIVATION)) count++;
