    private String alertId;
    private String msisdn;
    private AlertSeverity severity;
    private String alertType;
    private String message;
    private long alertTime;
    private boolean acknowledged;
    private long acknowledgedTime;

    public ProvisioningAlert() {}

    public String getAlertId() { return alertId; }
    public void setAlertId(String alertId) { this.alertId = alertId; }
    public String getMsisdn() { return msisdn; }
    public void setMsisdn(String msisdn) { this.msisdn = msisdn; }
    public AlertSeverity getSeverity() { return severity; }
    public void setSeverity(AlertSeverity severity) { this.severity = severity; }
    public String getAlertType() { return alertType; }
    public void setAlertType(String alertType) { this.alertType = alertType; }
    public String getMessage() { return message; }
    public void setMessage(String message) { this.message = message; }
    public long getAlertTime() { return alertTime; }
    public void setAlertTime(long alertTime) { this.alertTime = alertTime; }
    public boolean isAcknowledged() { return acknowledged; }
    public void setAcknowledged(boolean acknowledged) { this.acknowledged = acknowledged; }
    public long getAcknowledgedTime() { return acknowledgedTime; }
    public void setAcknowledgedTime(long acknowledgedTime) { this.acknowledgedTime = acknowledgedTime; }
}

package com.telecom.serviceprovisioning.model;

import java.util.Map;
import java.util.HashMap;

/**
 * Flexible DTO used across controllers/services in ServiceProvisioning.
 * This lightweight DTO stores arbitrary attributes as a map.
 */
public class GenericItem {
    private String id;
    private String name;
    private Map<String,Object> attributes = new HashMap<>();

    public GenericItem() {}
    public GenericItem(String id, String name) {
        this.id = id; this.name = name;
    }

    public String getId() { return id; }
    public void setId(String id) { this.id = id; }

    public String getName() { return name; }
    public void setName(String name) { this.name = name; }

    public Map<String,Object> getAttributes() { return attributes; }
    public void setAttributes(Map<String,Object> attributes) { this.attributes = attributes; }
}

package com.telecom.serviceprovisioning.repo;

import org.springframework.stereotype.Repository;
import java.util.*;
/**
 * Very small in-memory repository used for demonstration and local testing.
 * Replace with JPA, JDBC, or any real datastore for production.
 */
@Repository
public class InMemoryRepo {

    private final Map<String, Map<String,Object>> db = new HashMap<>();

    public void save(String id, Map<String,Object> item) {
        db.put(id, item);
    }

    public Map<String,Object> find(String id) {
        return db.get(id);
    }

    public List<Map<String,Object>> findAll() {
        return new ArrayList<>(db.values());
    }

    public void delete(String id) {
        db.remove(id);
    }

    public void clear() { db.clear(); }

    public int size() { return db.size(); }
}

