        ShoppingCart cart = cartStore.get(cartId);
        if (cart == null) {
            return null;
        }

        // Check if item already exists
        CartItem existingItem = cart.getItems().stream()
                .filter(item -> item.getProductId().equals(productId))
                .findFirst()
                .orElse(null);

        if (existingItem != null) {
