    public DeviceFinancingOption calculateFinancing(double devicePrice, FinancingTerms terms) {
        if (!featureFlagReader.isFeatureEnabled(ShoppingCartFeatureFlagConstants.CART_FINANCING_INSTALLMENT_PLANS)) {
            return null;
